import refrom PIL import Imageimport numpy as npimport matplotlib.pyplot as pltfrom wordcloud import WordCloud#读文件with open('./sklearn/Walden.txt','r') as f:    walden=f.read()#替换逗号、句号texts=re.sub('[(),.:*?"!\']','',walden)#分词words=texts.split(' ')#大小写转换lower_word=[i.lower() for i in words]#创建集合，去重复words_index=set(lower_word)#创建字典,词频统计dic={i:lower_word.count(i) for i in words_index}#字典按值排序,默认升序abc=sorted(dic.items(),key=lambda x:x[1],reverse=True)#轮廓图片image=Image.open('./sklearn/cloud.png')#读取图片像素矩阵graph=np.array(image)#设置词云的背景颜色和形状wc=WordCloud(background_color='White',mask=graph)#读取词频数据wc.generate_from_frequencies(dic)#设置图片显示内容plt.imshow(wc)#隐藏坐标轴plt.axis('off')#设置标题plt.title('walden')#显示图片plt.show()